import { useEffect } from "react";

const BookingWidget = () => {
  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://widget.simplybook.me/v2/widget/widget.js";
    script.async = true;

    script.onload = () => {
      if (window.SimplybookWidget) {
        window.simplybookPopup = new window.SimplybookWidget({
          widget_type: "popup",
          url: "https://vachatech.simplybook.it",
          theme: "simple",
          theme_settings: {
            sb_base_color: "#ff6600",
            sb_primary_color: "#ffffff",
            sb_text_color: "#333333"
          },
          app_config: {
            clear_session: 1
          }
        });
      }
    };

    document.body.appendChild(script);
  }, []);

  return null; // no need to render anything visually
};

export default BookingWidget;
