import React, { useEffect } from "react";

const BookingWidget = () => {
  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://widget.simplybook.me/v2/widget/widget.js";
    script.async = true;

    script.onload = () => {
      if (window.SimplybookWidget) {
        new window.SimplybookWidget({
          widget_type: "popup",
          url: "https://vachatech.simplybook.it",
          theme: "simple",
          theme_settings: {
            timeline_hide_unavailable: "1",
            hide_past_days: "1",
            sb_base_color: "#ff6600",
            sb_primary_color: "#ffffff",
            sb_text_color: "#333333"
          },
          app_config: {
            clear_session: 1
          },
          popup_width: "600px",
          popup_height: "700px",
          button_target_id: "booking-popup-btn"
        });
      }
    };

    document.body.appendChild(script);
  }, []);

  return null; // no need to render a div unless needed
};

export default BookingWidget;
